mapper.update:
UPDATE
	tbl_sboard 
SET 
	title = #{title}
	, content = #{content}
	, updatedate = NOW()
WHERE 
	brdno = #{brdno}

mapper.insert:
INSERT INTO tbl_sboardfile
(
	brdno
	, file_name
	, original_file_name
	, file_size
	, file_regdate
	, file_delat
)
VALUES
(
	#{brdno}
	, #{file_name}
	, #{original_file_name}
	, #{file_size}
	, NOW()
	, 'N'
)
